import React from 'react'
import Ex_pics from './ex_pics.json';
import Pagenotfound from './Pagenotfound';


const Ex_display=(props)=>
{
    console.log(props)

    const ex_data=(ex)=>
    {
        const {img,name,features,buy}=Ex_pics(ex)

        return(<>
               
        
                        {/* 1st right column */}  
                        <div className="col-lg-4" >
        
                            {/*  1st column card */}
                            <div className="card">
        
                                <img src={`${img}`}  
                                className="card-img-top mx-auto w-90 h-300 my-1 py-1 " />
        
                                <div className="card-body">
        
                                    <h5 className="card-title ">{name}</h5>
                                    <p className="b">
                                        {` Size : ${features["size"]}`}
                                        {` Price : ${features["size"]}`}
                                    </p>
                                    <a className="btn btn-primary ">{buy}</a>
        
                                </div>
        
                            </div>
                            {/*  1st column card */}
        
                        </div>
                        {/* 1st right column */}
                        
                     
            
        
        </>)

    }


    switch(props.match.params.name) 
    {
        case '1 layer explosion box': return(<> {ex_data(0)} </>); break;
        case '2 layer explosion box': return(<> {ex_data(1)} </>); break;
        case 'sqaure explosion box' : return(<> {ex_data(2)} </>); break;
        case 'heart explosion box'  : return(<> {ex_data(3)} </>); break;
        case 'hexa explosion box'   : return(<> {ex_data(4)} </>); break;
        default: return(<Pagenotfound />); break;
    }




}




export default Ex_display
