import React,{ useState , useEffect } from 'react'
import axios from 'axios';

import List_grp from './List_grp.js'




const url="http://localhost:3002/ex_pics"

const Home=()=>
{


    const [expics,setExpics]=useState( [ ] )
    const [sbpics,setSbpics]=useState( [ ] )


    useEffect( ()=>
    {
        axios.get(url).then((res)=> setExpics(res.data) )

    },[])


    return(<>

        <div className="container-fluid">
            <div className="row">

                {/* main left column */}
                <div className="col-lg-3">
                     
                    <List_grp />

                </div>
                {/* main left column */}


                {/* main right column */} 
                <div className="col-lg-9">

                    {/*  1st container */}
                    <div className="container">

                        <div className="text-center bg-in my-2 p-1 "><h2 id="ex">EXPLOSION BOX </h2></div>
                      
                        
                        <a href="#sb" className="mx-2">SCRAP BOOKS</a>
                        <a href="#gc" className="mx-2"> GREETING CARDS </a>
                        <a href="#pp" className="mx-2"> POP UP BOXES </a>
                        <a href="#wall" className="mx-2"> WALL DECORS </a> 
                        

                        {/* 1st ROW */}
                        <div className="row">

                            {
                                expics.map((e,index)=>
                                {
                                    return(<>

                                        {/* 1st right column */}  
                                        <div className="col-lg-4 my-2 " key={index}>

                                            {/*  1st column card */}
                                            <div className="card  border-0">

                                                <img src={`craft pics/explosion box/${e.img}`}  
                                                className="card-img-top mx-auto w-90 h-300 " />

                                                <div className="card-body text-center">

                                                    <h5 className="card-title text-uppercase ">{e.name}</h5>
                                                    
                                                        <ul type="none">
                                                            <li> {` Size : ${e.features["size"]}`} </li>
                                                            <li> {` Price : ${e.features["size"]}`} </li>
                                                        </ul>
                                                    
                                                    <a className="btn btn-primary ">{e.buy}</a>

                                                </div>

                                            </div>
                                            {/*  1st column card */}

                                        </div>
                                        {/* 1st right column */}
                                        
                                        
                                    </>)
                                })
                            }
                            

                        </div>
                        {/* 1st ROW */}

                    </div> 
                    {/* 1st container */}


                    {/*  1st container */}
                    <div className="container">

                        <div className="text-center bg-in my-2 p-1 "><h2 id="ex">EXPLOSION BOX </h2></div>
                      
                        
                        <a href="#sb" className="mx-2">SCRAP BOOKS</a>
                        <a href="#gc" className="mx-2"> GREETING CARDS </a>
                        <a href="#pp" className="mx-2"> POP UP BOXES </a>
                        <a href="#wall" className="mx-2"> WALL DECORS </a> 
                        

                        {/* 1st ROW */}
                        <div className="row">

                            {
                                expics.map((e,index)=>
                                {
                                    return(<>

                                        {/* 1st right column */}  
                                        <div className="col-lg-4 my-2 " key={index}>

                                            {/*  1st column card */}
                                            <div className="card  border-0">

                                                <img src={`craft pics/scrap books/${e.img}`}  
                                                className="card-img-top mx-auto w-90 h-300 " />

                                                <div className="card-body text-center">

                                                    <h5 className="card-title text-uppercase ">{e.name}</h5>
                                                    
                                                        <ul type="none">
                                                            <li> {` Size : ${e.features["size"]}`} </li>
                                                            <li> {` Price : ${e.features["size"]}`} </li>
                                                        </ul>
                                                    
                                                    <a className="btn btn-primary ">{e.buy}</a>

                                                </div>

                                            </div>
                                            {/*  1st column card */}

                                        </div>
                                        {/* 1st right column */}
                                        
                                        
                                    </>)
                                })
                            }
                            

                        </div>
                        {/* 1st ROW */}

                    </div> 
                    {/* 1st container */}


                    


                </div>
                {/* main right column */}

            </div>
            {/* main right row */}

        </div>
        {/* container */}



        {/* container 

        <Switch>
            <Route path={`${props.match.path}`} component={}
        </Switch>

        */}
        
    </>)



}

export default Home
