import React,{ useState , useEffect } from 'react'
import axios from 'axios';

import List_grp from './List_grp.js'




const url="http://localhost:3002/ex_pics"

const Home=()=>
{


    const [expics,setExpics]=useState( [ ] )


    useEffect( ()=>
    {
        axios.get(url).then((res)=> setExpics(res.data) )

    },[])


    return(<>

        <div className="container-flui">
            <div className="row">

                {/* main left column */}
                <div className="col-lg-3">
                     
                    <List_grp />

                </div>
                {/* main left column */}


                {/* main right column */} 
                <div className="col-lg-9">

                    {/*  1st container */}
                    <div className="container">

                        <div className="text-center bg-info my-2"><h2 id="ex">EXPLOSION BOX </h2></div>
                      
                        {/*
                        <a href="#sb" className="mx-2">SCRAP BOOKS</a>
                        <a href="#gc" className="mx-2"> GREETING CARDS </a>
                        <a href="#pp" className="mx-2"> POP UP BOXES </a>
                        <a href="#wall" className="mx-2"> WALL DECORS </a> 
                        */}

                        {/* 1st ROW */}
                        <div className="row">

                            {/* 1st right column */}
                            <div className="col-lg-4">

                                {
                                    expics.map((e,index)=>
                                    {
                                        return(<>
                                            
                                            {/*  1st column card */}
                                            <div className="card" key={index}>

                                                <img src={`${e.img}`}  
                                                className="card-img-top mx-auto w-90 h-300 my-1 py-1 " />

                                                <div className="card-body">

                                                    <h5 className="card-title ">{e.name}</h5>
                                                    <p className="b">
                                                        {` Size : ${e.features["size"]}`}
                                                        {` Price : ${e.features["size"]}`}
                                                    </p>
                                                    <a className="btn btn-primary ">{e.buy}</a>

                                                </div>

                                            </div>
                                            {/*  1st column card */}

                                        </>)
                                    })
                                }
                                
                            </div>
                            {/* 1st right column */}

                        </div>
                        {/* 1st ROW */}

                    </div> 
                    {/* 1st container */}


                </div>
                {/* main right column */}

            </div>
            {/* main right row */}

        </div>
        {/* container */}



        {/* container 

        <Switch>
            <Route path={`${props.match.path}`} component={}
        </Switch>

        */}
        
    </>)

}

export default Home
