import React,{ useState , useEffect } from 'react'
import axios from 'axios';

import List_grp from './List_grp.js'




const url1="http://localhost:3002/ex_pics"
const url2="http://localhost:3002/sb_pics"
const url3="http://localhost:3002/gc_pics"
const url4="http://localhost:3002/pp_pics"
const url5="http://localhost:3002/wd_pics"


const Home=()=>
{


    const [expics,setExpics]=useState( [ ] )

    const [sbpics,setSbpics]=useState( [ ] )

    const [gcpics,setGcpics]=useState( [ ] )

    const [Pp_pics,setPp_pics]=useState( [ ] )

    const [wdpics,setWdpics]=useState( [ ] )


    useEffect( ()=>
    {
        axios.get(url1).then((res)=> setExpics(res.data) )
        axios.get(url2).then((res)=> setSbpics(res.data) )
        axios.get(url3).then((res)=> setGcpics(res.data) )
        axios.get(url4).then((res)=> setPp_pics(res.data) )
        axios.get(url5).then((res)=> setSbpics(res.data) )

    },[])


    return(<>

        <div className="container-fluid">
            <div className="row">

                {/* main left column */}
                <div className="col-lg-3">
                     
                    <List_grp />

                </div>
                {/* main left column */}


                {/* main right column */} 
                <div className="col-lg-9">

                    {/*  1st container */}
                    <div className="container">

                        <div className="text-center bg-in my-2 p-1 "><h2 id="ex">EXPLOSION BOX </h2></div>
                      
                        
                        <a href="#sb" className="mx-2">SCRAP BOOKS</a>
                        <a href="#gc" className="mx-2"> GREETING CARDS </a>
                        <a href="#pp" className="mx-2"> POP UP BOXES </a>
                        <a href="#wall" className="mx-2"> WALL DECORS </a> 
                        

                        {/* 1st ROW */}
                        <div className="row">

                            {
                                expics.map((e,index)=>
                                {
                                    return(<>

                                        {/* 1st right column */}  
                                        <div className="col-lg-4 my-2 " key={index}>

                                            {/*  1st column card */}
                                            <div className="card  border-0">

                                                <img src={`craft pics/explosion box/${e.img}`}  
                                                className="card-img-top mx-auto w-90 h-300 " />

                                                <div className="card-body text-center">

                                                    <h5 className="card-title text-uppercase ">{e.name}</h5>
                                                    
                                                        <ul type="none">
                                                            <li> {` Size : ${e.features["size"]}`} </li>
                                                            <li> {` Price : ${e.features["size"]}`} </li>
                                                        </ul>
                                                    
                                                    <a className="btn btn-primary ">{e.buy}</a>

                                                </div>

                                            </div>
                                            {/*  1st column card */}

                                        </div>
                                        {/* 1st right column */}
                                        
                                        
                                    </>)
                                })
                            }
                            

                        </div>
                        {/* 1st ROW */}

                    </div> 
                    {/* 1st container */}


                    {/*  1st container */}
                    <div className="container">

                        <div className="text-center bg-in my-2 p-1 "><h2 id="ex">EXPLOSION BOX </h2></div>
                      
                        
                        <a href="#sb" className="mx-2">SCRAP BOOKS</a>
                        <a href="#gc" className="mx-2"> GREETING CARDS </a>
                        <a href="#pp" className="mx-2"> POP UP BOXES </a>
                        <a href="#wall" className="mx-2"> WALL DECORS </a> 
                        

                        {/* 1st ROW */}
                        <div className="row">

                            {
                                sbpics.map((s,index)=>
                                {
                                    return(<>

                                        {/* 1st right column */}  
                                        <div className="col-lg-4 my-2 " key={index}>

                                            {/*  1st column card */}
                                            <div className="card  border-0">

                                                <img src={`craft pics/scrap books/${s.img}`}  
                                                className="card-img-top mx-auto w-90 h-300 " />

                                                <div className="card-body text-center">

                                                    <h5 className="card-title text-uppercase ">{s.name}</h5>
                                                    
                                                        <ul type="none">
                                                            <li> {` Size : ${s.features["size"]}`} </li>
                                                            <li> {` Price : ${s.features["size"]}`} </li>
                                                        </ul>
                                                    
                                                    <a className="btn btn-primary ">{s.buy}</a>

                                                </div>

                                            </div>
                                            {/*  1st column card */}

                                        </div>
                                        {/* 1st right column */}
                                        
                                        
                                    </>)
                                })
                            }
                            

                        </div>
                        {/* 1st ROW */}

                    </div> 
                    {/* 1st container */}


                    {/*  1st container */}
                    <div className="container">

                        <div className="text-center bg-in my-2 p-1 "><h2 id="ex">EXPLOSION BOX </h2></div>
                      
                        
                        <a href="#sb" className="mx-2">SCRAP BOOKS</a>
                        <a href="#gc" className="mx-2"> GREETING CARDS </a>
                        <a href="#pp" className="mx-2"> POP UP BOXES </a>
                        <a href="#wall" className="mx-2"> WALL DECORS </a> 
                        

                        {/* 1st ROW */}
                        <div className="row">

                            {
                                sbpics.map((s,index)=>
                                {
                                    return(<>

                                        {/* 1st right column */}  
                                        <div className="col-lg-4 my-2 " key={index}>

                                            {/*  1st column card */}
                                            <div className="card  border-0">

                                                <img src={`craft pics/scrap books/${s.img}`}  
                                                className="card-img-top mx-auto w-90 h-300 " />

                                                <div className="card-body text-center">

                                                    <h5 className="card-title text-uppercase ">{s.name}</h5>
                                                    
                                                        <ul type="none">
                                                            <li> {` Size : ${s.features["size"]}`} </li>
                                                            <li> {` Price : ${s.features["size"]}`} </li>
                                                        </ul>
                                                    
                                                    <a className="btn btn-primary ">{s.buy}</a>

                                                </div>

                                            </div>
                                            {/*  1st column card */}

                                        </div>
                                        {/* 1st right column */}
                                        
                                        
                                    </>)
                                })
                            }
                            

                        </div>
                        {/* 1st ROW */}

                    </div> 
                    {/* 1st container */}


                    


                </div>
                {/* main right column */}

            </div>
            {/* main right row */}

        </div>
        {/* container */}



        {/* container 

        <Switch>
            <Route path={`${props.match.path}`} component={}
        </Switch>

        */}
        
    </>)



}

export default Home
